import './App.css';
import React, {useState} from 'react';
import axios from "axios";

function App() {
  const [Cedula, setCedula] = useState(0);
  const [Nombre, setNombre] = useState('');
  const [Apellidos, setApellidos] = useState('');
  const [Sueldo, setSueldo] = useState(0);
  const [Anios, setAnios] = useState(0);
  const [Cargo, setCargo] = useState('');




  const add = ()=>{
    alert(Cargo)
    console.log()
    axios.post("http://localhost:3001/create",{
      Cedula:Cedula,
      Nombre:Nombre,
      Apellidos:Apellidos,
      Sueldo:Sueldo,
      Cargo:Cargo,
      Anios:Anios,

    }).then(()=>{
      alert("Se agregó el empleado correctamente");
    });
  }
  return (
    <div className="App">
      <div className="datos">
        <label>Cedula: <input type="text"
        onChange={(event) => {
          setCedula(event.target.value);
        }}
        /></label><br />
        <label>Nombre: <input type="text"
        onChange={(event) => {
          setNombre(event.target.value);
        }}
        /></label><br />

        <label>Apellidos: <input type="text"
        onChange={(event) => {
          setApellidos(event.target.value);
        }}
        /></label><br />

        <label>Cargo: <input type="text"
        onChange={(event) => {
          setCargo(event.target.value);
        }}
        /></label><br />



        <label>Sueldo: <input type="text" 
        onChange={(event) => {
          setSueldo(event.target.value);
        }}
        /></label><br />

        <label>Edad: <input type="text" 
        onChange={(event) => {
          setAnios(event.target.value);
        }}
        /></label><br />


        <button onClick={add}>Registrar</button>
      </div>
    </div>
  );
}

export default App;
